import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Header from "../components/Header";
import "../styles/Dashboard.css";

import { FaImage } from "react-icons/fa";
import { get_project_data } from "../services/proposal_service"; // <-- new function

export default function AllProjectsPage() {
  const navigate = useNavigate();
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const projectData = await get_project_data();
        setProjects(projectData);
      } catch (err) {
        console.error("Failed to fetch projects", err);
      }
    };

    fetchProjects();
  }, []);

  return (
    <main className="dashboard-wrapper">
      <Header />

      <section className="dashboard-container">
        {projects.map((project) => (
          <ProjectCard
            key={project.project_id}
            project={project}
            onClick={() =>
              navigate(`/project_details/`, {
                state: { project_id: project.project_id },
              })
            }
          />
        ))}
      </section>
    </main>
  );
}

function ProjectCard({ project, onClick }) {
  const [imgError, setImgError] = React.useState(false);

  const isImageValid =
    project.link_image && project.link_image.trim() !== "" && !imgError;

  return (
    <article className="proposal-card" onClick={onClick}>
      {isImageValid ? (
        <img
          src={project.link_image}
          onError={() => setImgError(true)}
          alt={project.title}
          className="proposal-image"
        />
      ) : (
        <div className="proposal-image fallback-icon">
          <FaImage size={48} color="#aaa" />
        </div>
      )}
      <h3 className="proposal-title">{project.title}</h3>
      <p className="proposal-summary">{project.description}</p>
    </article>
  );
}
