export async function flag_project(project_id, user_id, reason, description) {
    try {
      const response = await fetch('/projects/flag', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          project_id,
          user_id,
          reason,
          description
        }),
      });
  
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || "Failed to flag project");
      }
  
      return await response.json();
    } catch (err) {
      console.error("Flagging failed:", err);
      throw err;
    }
  }
